import React, { useState, useEffect } from 'react';
import api from '../utils/api';

const QRCodeVerification = () => {
  const [qrCode, setQrCode] = useState(null);
  const [verificationCode, setVerificationCode] = useState(null);
  const [isVerified, setIsVerified] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const generateQR = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await api.get('/mobile/generate-qr');
      setQrCode(response.data.qr_code);
      setVerificationCode(response.data.verification_code);

      // Start checking for verification
      checkVerification(response.data.verification_code);
    } catch (err) {
      setError('Failed to generate QR code');
    } finally {
      setLoading(false);
    }
  };

  const checkVerification = (code) => {
    const interval = setInterval(async () => {
      try {
        const response = await api.get(`/mobile/check-verification/${code}`);
        if (response.data.verified) {
          setIsVerified(true);
          clearInterval(interval);
        } else if (response.data.expired) {
          setError('QR code expired. Please generate a new one.');
          clearInterval(interval);
        }
      } catch (err) {
        clearInterval(interval);
      }
    }, 2000);

    // Stop checking after 5 minutes
    setTimeout(() => clearInterval(interval), 300000);
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <h3 className="text-lg font-semibold mb-4">Mobile App Verification</h3>

      {!qrCode && !loading && (
        <div className="text-center">
          <p className="text-gray-600 mb-4">
            Generate a QR code to verify your mobile app access
          </p>
          <button
            onClick={generateQR}
            className="bg-green-600 text-white px-6 py-2 rounded-lg hover:bg-green-700"
          >
            Generate QR Code
          </button>
        </div>
      )}

      {loading && (
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-green-600 mx-auto"></div>
          <p className="mt-2 text-gray-600">Generating QR code...</p>
        </div>
      )}

      {qrCode && !isVerified && (
        <div className="text-center">
          <img 
            src={qrCode} 
            alt="QR Code" 
            className="mx-auto mb-4 border rounded-lg"
          />
          <p className="text-sm text-gray-600 mb-2">
            Scan this QR code with your mobile app
          </p>
          <p className="text-xs text-gray-500">
            Code expires in 5 minutes
          </p>
          <button
            onClick={generateQR}
            className="mt-4 text-green-600 hover:text-green-700 text-sm"
          >
            Generate New Code
          </button>
        </div>
      )}

      {isVerified && (
        <div className="text-center">
          <div className="text-green-600 mb-2">
            <svg className="w-12 h-12 mx-auto" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
            </svg>
          </div>
          <p className="text-green-600 font-semibold">Mobile App Verified!</p>
          <p className="text-sm text-gray-600 mt-2">
            Your mobile device has been successfully verified
          </p>
        </div>
      )}

      {error && (
        <div className="text-center">
          <p className="text-red-600 mb-4">{error}</p>
          <button
            onClick={generateQR}
            className="bg-green-600 text-white px-6 py-2 rounded-lg hover:bg-green-700"
          >
            Try Again
          </button>
        </div>
      )}
    </div>
  );
};

export default QRCodeVerification;
