import React, { useState, useEffect } from 'react';
import { Bell, Truck, Recycle, MapPin, TrendingUp } from 'lucide-react';
import axios from 'axios';

const Dashboard = () => {
  const [stats, setStats] = useState({ totalBins: 0, collectedToday: 0, recycledWeight: 0, pendingCollections: 0 });
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showNotifications, setShowNotifications] = useState(false);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      const statsResponse = await axios.get('http://localhost:5003/api/dashboard/stats');
      const notificationsResponse = await axios.get('http://localhost:5003/api/notifications');
      setStats(statsResponse.data);
      setNotifications(notificationsResponse.data);
    } catch (error) {
      console.error('Error loading data:', error);
    }
    setLoading(false);
  };

  if (loading) {
    return <div className="flex items-center justify-center min-h-screen">Loading...</div>;
  }

  return (
    <div className="pb-20 bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="bg-primary text-white p-6 rounded-b-3xl">
        <div className="flex justify-between items-center mb-4">
          <div>
            <h1 className="text-2xl font-bold">TakaTrack</h1>
            <p className="opacity-90">Smart Waste Management</p>
          </div>
          <div className="relative">
            <button onClick={() => setShowNotifications(!showNotifications)} className="p-2 hover:bg-white/10 rounded-full transition-colors">
              <Bell size={24} />
              {notifications.length > 0 && (
                <span className="absolute -top-1 -right-1 bg-red-500 text-xs rounded-full h-5 w-5 flex items-center justify-center">
                  {notifications.length}
                </span>
              )}
            </button>
          </div>
        </div>
      </div>

      {showNotifications && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-start justify-center pt-20">
          <div className="bg-white rounded-xl w-full max-w-md mx-4 max-h-96 overflow-y-auto">
            <div className="p-4 border-b flex justify-between items-center">
              <h3 className="text-lg font-semibold">Notifications</h3>
              <button onClick={() => setShowNotifications(false)} className="text-gray-500">Ã—</button>
            </div>
            <div className="p-4">
              {notifications.length === 0 ? (
                <p className="text-gray-500 text-center py-4">No notifications</p>
              ) : (
                notifications.map((notification, index) => (
                  <div key={index} className="p-3 bg-gray-50 rounded-lg mb-3">
                    <p className="text-sm font-medium">{notification.title}</p>
                    <p className="text-xs text-gray-600">{notification.message}</p>
                    <p className="text-xs text-gray-500">{notification.time}</p>
                  </div>
                ))
              )}
            </div>
          </div>
        </div>
      )}

      {/* Stats Cards */}
      <div className="p-4 -mt-8">
        <div className="grid grid-cols-2 gap-4 mb-6">
          <div className="bg-white p-4 rounded-xl shadow-sm">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-600 text-sm">Total Bins</p>
                <p className="text-2xl font-bold text-gray-900">{stats.totalBins}</p>
              </div>
              <div className="bg-blue-100 p-2 rounded-lg">
                <MapPin className="h-6 w-6 text-blue-600" />
              </div>
            </div>
          </div>

          <div className="bg-white p-4 rounded-xl shadow-sm">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-600 text-sm">Collected Today</p>
                <p className="text-2xl font-bold text-gray-900">{stats.collectedToday}</p>
              </div>
              <div className="bg-green-100 p-2 rounded-lg">
                <Truck className="h-6 w-6 text-green-600" />
              </div>
            </div>
          </div>

          <div className="bg-white p-4 rounded-xl shadow-sm">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-600 text-sm">Recycled (kg)</p>
                <p className="text-2xl font-bold text-gray-900">{stats.recycledWeight}</p>
              </div>
              <div className="bg-primary/10 p-2 rounded-lg">
                <Recycle className="h-6 w-6 text-primary" />
              </div>
            </div>
          </div>

          <div className="bg-white p-4 rounded-xl shadow-sm">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-600 text-sm">Pending</p>
                <p className="text-2xl font-bold text-gray-900">{stats.pendingCollections}</p>
              </div>
              <div className="bg-orange-100 p-2 rounded-lg">
                <TrendingUp className="h-6 w-6 text-orange-600" />
              </div>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-sm p-4">
          <h3 className="text-lg font-semibold mb-4">Recent Activity</h3>
          {notifications.slice(0, 5).map((notification, index) => (
            <div key={index} className="flex items-center p-3 bg-gray-50 rounded-lg mb-3">
              <Bell size={16} className="text-primary mr-3" />
              <div>
                <p className="text-sm font-medium">{notification.title}</p>
                <p className="text-xs text-gray-600">{notification.time}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
