import React, { useState, useEffect } from 'react';
import { Plus, Calendar, MapPin, Truck, CheckCircle } from 'lucide-react';
import axios from 'axios';
import { toast } from 'react-toastify';

const WasteCollection = () => {
  const [collections, setCollections] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    location: '',
    wasteType: 'general',
    scheduledDate: '',
    priority: 'medium'
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchCollections();
  }, []);

  const fetchCollections = async () => {
    try {
      const response = await axios.get('/api/collections');
      setCollections(response.data);
    } catch (error) {
      console.error('Error fetching collections:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/collections', formData);
      toast.success('Collection scheduled successfully!');
      setShowForm(false);
      setFormData({ location: '', wasteType: 'general', scheduledDate: '', priority: 'medium' });
      fetchCollections();
    } catch (error) {
      toast.error('Failed to schedule collection');
    }
  };

  const updateStatus = async (id, status) => {
    try {
      await axios.patch(`/api/collections/${id}`, { status });
      toast.success('Status updated successfully!');
      fetchCollections();
    } catch (error) {
      toast.error('Failed to update status');
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed': return 'bg-green-100 text-green-800';
      case 'in-progress': return 'bg-blue-100 text-blue-800';
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="pb-20 bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="bg-white p-4 border-b">
        <div className="flex justify-between items-center">
          <h1 className="text-xl font-bold">Waste Collection</h1>
          <button
            onClick={() => setShowForm(true)}
            className="bg-primary text-white p-2 rounded-full"
          >
            <Plus size={20} />
          </button>
        </div>
      </div>

      {/* Collection Form Modal */}
      {showForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
          <div className="bg-white rounded-xl w-full max-w-md p-6">
            <h2 className="text-lg font-bold mb-4">Schedule Collection</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
              <input
                type="text"
                placeholder="Location"
                value={formData.location}
                onChange={(e) => setFormData({...formData, location: e.target.value})}
                className="w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-primary"
                required
              />

              <select
                value={formData.wasteType}
                onChange={(e) => setFormData({...formData, wasteType: e.target.value})}
                className="w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-primary"
              >
                <option value="general">General Waste</option>
                <option value="recyclable">Recyclable</option>
                <option value="organic">Organic</option>
                <option value="hazardous">Hazardous</option>
              </select>

              <input
                type="datetime-local"
                value={formData.scheduledDate}
                onChange={(e) => setFormData({...formData, scheduledDate: e.target.value})}
                className="w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-primary"
                required
              />

              <select
                value={formData.priority}
                onChange={(e) => setFormData({...formData, priority: e.target.value})}
                className="w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-primary"
              >
                <option value="low">Low Priority</option>
                <option value="medium">Medium Priority</option>
                <option value="high">High Priority</option>
              </select>

              <div className="flex gap-3">
                <button
                  type="button"
                  onClick={() => setShowForm(false)}
                  className="flex-1 py-3 border border-gray-300 rounded-lg"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="flex-1 py-3 bg-primary text-white rounded-lg"
                >
                  Schedule
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Collections List */}
      <div className="p-4">
        <div className="space-y-4">
          {collections.map((collection) => (
            <div key={collection.id} className="bg-white rounded-xl p-4 shadow-sm">
              <div className="flex justify-between items-start mb-3">
                <div className="flex-1">
                  <div className="flex items-center gap-2 mb-2">
                    <MapPin size={16} className="text-gray-500" />
                    <span className="font-medium">{collection.location}</span>
                  </div>
                  <div className="flex items-center gap-2 mb-2">
                    <Calendar size={16} className="text-gray-500" />
                    <span className="text-sm text-gray-600">
                      {new Date(collection.scheduledDate).toLocaleDateString()}
                    </span>
                  </div>
                  <div className="flex items-center gap-2">
                    <Truck size={16} className="text-gray-500" />
                    <span className="text-sm text-gray-600 capitalize">
                      {collection.wasteType}
                    </span>
                  </div>
                </div>
                <span className={`px-3 py-1 rounded-full text-xs font-medium ${getStatusColor(collection.status)}`}>
                  {collection.status}
                </span>
              </div>

              {collection.status === 'pending' && (
                <div className="flex gap-2">
                  <button
                    onClick={() => updateStatus(collection.id, 'in-progress')}
                    className="flex-1 py-2 bg-blue-500 text-white rounded-lg text-sm"
                  >
                    Start Collection
                  </button>
                  <button
                    onClick={() => updateStatus(collection.id, 'completed')}
                    className="flex-1 py-2 bg-green-500 text-white rounded-lg text-sm flex items-center justify-center gap-1"
                  >
                    <CheckCircle size={16} />
                    Complete
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default WasteCollection;
