import React, { useState, useEffect } from 'react';
import { MapPin, Navigation, Filter, Truck } from 'lucide-react';
import axios from 'axios';

const MapView = () => {
  const [bins, setBins] = useState([]);
  const [trucks, setTrucks] = useState([]);
  const [filter, setFilter] = useState('all');
  const [userLocation, setUserLocation] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchMapData();
    getCurrentLocation();
  }, []);

  const fetchMapData = async () => {
    try {
      const [binsRes, trucksRes] = await Promise.all([
        axios.get('/api/bins'),
        axios.get('/api/trucks/locations')
      ]);
      setBins(binsRes.data);
      setTrucks(trucksRes.data);
    } catch (error) {
      console.error('Error fetching map data:', error);
    } finally {
      setLoading(false);
    }
  };

  const getCurrentLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude
          });
        },
        (error) => {
          console.error('Error getting location:', error);
        }
      );
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'full': return 'bg-red-500';
      case 'half': return 'bg-yellow-500';
      case 'empty': return 'bg-green-500';
      default: return 'bg-gray-500';
    }
  };

  const filteredBins = bins.filter(bin => {
    if (filter === 'all') return true;
    return bin.status === filter;
  });

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="pb-20 bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="bg-white p-4 border-b">
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-xl font-bold">Map View</h1>
          <button className="p-2 bg-primary text-white rounded-full">
            <Navigation size={20} />
          </button>
        </div>

        {/* Filter Buttons */}
        <div className="flex gap-2 overflow-x-auto">
          {['all', 'full', 'half', 'empty'].map((status) => (
            <button
              key={status}
              onClick={() => setFilter(status)}
              className={`px-4 py-2 rounded-full text-sm font-medium whitespace-nowrap ${
                filter === status
                  ? 'bg-primary text-white'
                  : 'bg-gray-100 text-gray-600'
              }`}
            >
              {status === 'all' ? 'All Bins' : `${status.charAt(0).toUpperCase() + status.slice(1)} Bins`}
            </button>
          ))}
        </div>
      </div>

      {/* Map Placeholder - In a real app, this would be a proper map component */}
      <div className="relative bg-gray-200 h-64 m-4 rounded-xl overflow-hidden">
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="text-center">
            <MapPin size={48} className="text-gray-400 mx-auto mb-2" />
            <p className="text-gray-600">Interactive Map</p>
            <p className="text-sm text-gray-500">Showing {filteredBins.length} bins</p>
          </div>
        </div>

        {/* Mock map pins */}
        <div className="absolute top-4 left-4 w-3 h-3 bg-red-500 rounded-full"></div>
        <div className="absolute top-12 right-8 w-3 h-3 bg-green-500 rounded-full"></div>
        <div className="absolute bottom-8 left-12 w-3 h-3 bg-yellow-500 rounded-full"></div>
        <div className="absolute bottom-4 right-4 w-3 h-3 bg-blue-500 rounded-full"></div>
      </div>

      {/* Legend */}
      <div className="mx-4 mb-4 bg-white rounded-xl p-4">
        <h3 className="font-semibold mb-3">Legend</h3>
        <div className="grid grid-cols-2 gap-3">
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 bg-red-500 rounded-full"></div>
            <span className="text-sm">Full Bins</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 bg-yellow-500 rounded-full"></div>
            <span className="text-sm">Half Full</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 bg-green-500 rounded-full"></div>
            <span className="text-sm">Empty Bins</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-3 h-3 bg-blue-500 rounded-full"></div>
            <span className="text-sm">Collection Trucks</span>
          </div>
        </div>
      </div>

      {/* Bins List */}
      <div className="mx-4 space-y-3">
        <h3 className="font-semibold">Nearby Bins</h3>
        {filteredBins.map((bin) => (
          <div key={bin.id} className="bg-white rounded-xl p-4 shadow-sm">
            <div className="flex justify-between items-start">
              <div className="flex-1">
                <div className="flex items-center gap-2 mb-2">
                  <MapPin size={16} className="text-gray-500" />
                  <span className="font-medium">{bin.location}</span>
                </div>
                <p className="text-sm text-gray-600 mb-1">
                  Type: {bin.type}
                </p>
                <p className="text-sm text-gray-600">
                  Last collected: {new Date(bin.lastCollected).toLocaleDateString()}
                </p>
              </div>
              <div className="text-right">
                <div className={`w-4 h-4 rounded-full ${getStatusColor(bin.status)} mb-2`}></div>
                <span className="text-xs text-gray-500 capitalize">{bin.status}</span>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Active Trucks */}
      {trucks.length > 0 && (
        <div className="mx-4 mt-6 space-y-3">
          <h3 className="font-semibold">Active Trucks</h3>
          {trucks.map((truck) => (
            <div key={truck.id} className="bg-white rounded-xl p-4 shadow-sm">
              <div className="flex items-center gap-3">
                <div className="bg-blue-100 p-2 rounded-full">
                  <Truck size={20} className="text-blue-600" />
                </div>
                <div className="flex-1">
                  <p className="font-medium">Truck #{truck.id}</p>
                  <p className="text-sm text-gray-600">Driver: {truck.driver}</p>
                  <p className="text-sm text-gray-600">Status: {truck.status}</p>
                </div>
                <div className="text-right">
                  <p className="text-sm font-medium text-green-600">
                    {truck.collectionsToday} collections
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MapView;
