import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import { Smartphone, CheckCircle, XCircle } from 'lucide-react';

const MobileVerify = () => {
  const [searchParams] = useSearchParams();
  const [status, setStatus] = useState('verifying'); // verifying, success, error
  const [message, setMessage] = useState('');

  useEffect(() => {
    const verifyCode = async () => {
      const code = searchParams.get('code');
      const userId = searchParams.get('user');

      if (!code) {
        setStatus('error');
        setMessage('Invalid verification link');
        return;
      }

      try {
        const response = await fetch(`${process.env.REACT_APP_API_URL}/api/mobile/verify-mobile`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ code }),
        });

        const data = await response.json();

        if (response.ok) {
          setStatus('success');
          setMessage('Mobile app verified successfully!');
        } else {
          setStatus('error');
          setMessage(data.error || 'Verification failed');
        }
      } catch (error) {
        setStatus('error');
        setMessage('Network error. Please try again.');
      }
    };

    verifyCode();
  }, [searchParams]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 to-blue-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl shadow-xl p-8 max-w-md w-full text-center">
        <div className="mb-6">
          <Smartphone size={64} className="mx-auto text-green-600 mb-4" />
          <h1 className="text-2xl font-bold text-gray-800 mb-2">
            TakaTrack Mobile Verification
          </h1>
        </div>

        {status === 'verifying' && (
          <div>
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-green-600 mx-auto mb-4"></div>
            <p className="text-gray-600">Verifying your mobile device...</p>
          </div>
        )}

        {status === 'success' && (
          <div>
            <CheckCircle size={48} className="mx-auto text-green-600 mb-4" />
            <p className="text-green-600 font-semibold text-lg mb-2">Success!</p>
            <p className="text-gray-600 mb-6">{message}</p>
            <p className="text-sm text-gray-500">
              You can now close this page and return to the app.
            </p>
          </div>
        )}

        {status === 'error' && (
          <div>
            <XCircle size={48} className="mx-auto text-red-600 mb-4" />
            <p className="text-red-600 font-semibold text-lg mb-2">Verification Failed</p>
            <p className="text-gray-600 mb-6">{message}</p>
            <p className="text-sm text-gray-500">
              Please generate a new QR code and try again.
            </p>
          </div>
        )}

        <div className="mt-8 pt-6 border-t border-gray-200">
          <p className="text-xs text-gray-400">
            TakaTrack - Smart Waste Management
          </p>
        </div>
      </div>
    </div>
  );
};

export default MobileVerify;
